sys_script_include.description:
This Script Include provides automated plugin update functionality for ServiceNow instances. It can be called programmatically or via the UI Action to update multiple plugins in a controlled manner.
Key Features: - Processes plugins in configurable batch sizes - Supports a skip list for excluding specific plugins - Provides detailed logging of the update process - Returns a summary of updates, skips, and failures - Can be scheduled to run automatically or triggered manually
Usage: var updater = new global.x_snc_update_all.UpdateAllPlugins(); var result = updater();
Configuration: - x_snc_update_all.plugin.update.batch.size: Maximum number of plugins to update in one run - x_snc_update_all.plugin.update.skip.list: Comma-separated list of plugins to skip
Returns: { updated: [array of successfully updated plugin names], skipped: [array of skipped plugin names], failed: [array of failed plugin names with error details], duration: total execution time in seconds }

sys_script_include.script:
var UpdateAllPlugins = function() {
    var scriptStartTime = new Date();
    gs.info('Plugin update process (x_snc_update_all) ▸ STARTED at ' + scriptStartTime);

    // Refresh store data before checking for updates
    try {
        gs.info('Plugin update process (x_snc_update_all) ▸ Refreshing store data (synchronously via worker)...');

        var worker = new GlideScriptedHierarchicalWorker();
        worker.setProgressName('Checking for application updates (via UpdateAllPlugins script)');
        worker.setScriptIncludeName('AppStoreAjax'); // System script include for app store operations
        worker.setScriptIncludeMethod('checkForUpdates'); // Standard method to refresh app updates
        // worker.putMethodArg('sysparm_description', ''); // Optional: usually empty for system check
        worker.start();
        var trackerId = worker.getProgressID();

        var maxWaitTimeMs = 60000; // Maximum wait time: 60 seconds
        var waitedTimeMs = 0;
        var pollIntervalMs = 2000; // Check status every 2 seconds

        if (trackerId) {
            var progressWorkerGR = new GlideRecord('sys_progress_worker');
            while (waitedTimeMs < maxWaitTimeMs) {
                if (progressWorkerGR.get(trackerId)) {
                    var state = progressWorkerGR.getValue('state');
                    if (state == 'complete' || state == 'failed' || state == 'cancelled') {
                        if (state == 'complete') {
                            gs.info('Plugin update process (x_snc_update_all) ▸ App update check worker completed successfully.');
                        } else {
                            gs.error('Plugin update process (x_snc_update_all) ▸ App update check worker finished with state: ' + state + '. Message: ' + progressWorkerGR.getValue('message'));
                        }
                        break; // Exit loop
                    }
                } else {
                    gs.warn('Plugin update process (x_snc_update_all) ▸ Could not find progress worker with ID: ' + trackerId + '. Assuming completion or issue.');
                    break; // Exit loop if tracker disappears
                }
                gs.sleep(pollIntervalMs);
                waitedTimeMs += pollIntervalMs;
            }
            if (waitedTimeMs >= maxWaitTimeMs) {
                gs.warn('Plugin update process (x_snc_update_all) ▸ App update check worker timed out after ' + (maxWaitTimeMs / 1000) + ' seconds.');
            }
        } else {
            gs.warn('Plugin update process (x_snc_update_all) ▸ Failed to start app update check worker or get its progress ID.');
        }

        gs.info('Plugin update process (x_snc_update_all) ▸ Store data refresh attempt finished.');
    } catch (ex) {
        gs.error('Plugin update process (x_snc_update_all) ▸ Exception during store data refresh: ' + ex);
        // Continue with the update process even if refresh fails
    }

    var skipListProperty = gs.getProperty('x_snc_update_all.plugin.update.skip.list', '');
    var skip = (skipListProperty || '')
            .split(',')
            .map(function (s) {return s.trim();})
            .filter(function(s) {return s.length > 0;});

    if (skip.length > 0) {
        gs.info('Plugin update process (x_snc_update_all) ▸ Skipping: ' + skip.join(', '));
    }

    var gr = new GlideRecord('sys_store_app');
    gr.addEncodedQuery('hide_on_ui=false^update_available=true');
    gr.orderBy('name');
    gr.query();

    var upgrader = new sn_appclient.AppUpgrader();
    var updated = [];
    var skipped = [];
    var failed = [];
    var total = gr.getRowCount();

    gs.info('Plugin update process (x_snc_update_all) ▸ Found ' + total + ' updatable plugins after refresh attempt.');

    var maxUpdatesPerRun = parseInt(gs.getProperty('x_snc_update_all.plugin.update.batch.size', '10')) || 10;
    var updateCount = 0;

    while (gr.next() && updateCount < maxUpdatesPerRun) {
        var id = gr.getUniqueValue();
        var source = gr.getValue('source');
        var name = gr.getValue('name');
        var currentVer = gr.getValue('version');
        var newVersion = gr.getValue('latest_version');

        if (skip.indexOf(source) > -1 || skip.indexOf(id) > -1) {
            skipped.push(name);
            gs.info('Plugin update (x_snc_update_all) ▸ SKIPPED ' + name + ' (in skip list)');
            continue;
        }

        try {
            gs.info('Plugin update (x_snc_update_all) ▸ QUEUED ' + name + ' [' + currentVer + ' → ' + newVersion + ']');
            var updateStartTime = new Date();
            upgrader.upgrade(id, newVersion, false);
            var updateDuration = (new Date() - updateStartTime) / 1000;
            updated.push(name);
            updateCount++;
            gs.info('Plugin update (x_snc_update_all) ▸ COMPLETE ' + name + ' (took ' + updateDuration + 's)');
        } catch (ex) {
            var errorInfo = name + ' (' + (ex.message || String(ex)) + ')';
            failed.push(errorInfo);
            gs.error('Plugin update (x_snc_update_all) ▸ FAILED ' + name + ' – ' + ex);
        }
    }

    if (updateCount >= maxUpdatesPerRun && total > updateCount) {
        gs.info('Plugin update process (x_snc_update_all) ▸ Processed batch of ' + maxUpdatesPerRun + '. Remaining: ' + (total - updateCount));
    }

    var totalDuration = (new Date() - scriptStartTime) / 1000;
    gs.info('Plugin update summary (x_snc_update_all) ▸ Updated: ' + updated.length +
            ', Skipped: ' + skipped.length +
            ', Failed: '  + failed.length + 
            ', Duration: ' + totalDuration + 's');

    if (failed.length > 0) {
        gs.info('Plugin update failures (x_snc_update_all) ▸ ' + failed.join(', '));
    }

    return {
        updated: updated,
        skipped: skipped,
        failed: failed,
        duration: totalDuration
    };
};
